figure.programlisting:
SELECT foo.id 
FROM (
SELECT pipe_schema_items.groupid AS id
     , (st_multi
           (st_linemerge
                 (st_union
                       (pipe_schema_items.geometry)
                 )
           )
     )::geometry
     (MultiLineString
           ,21781
     ) AS geometry 
  FROM distribution.pipe_schema_items 
GROUP BY pipe_schema_items.groupid
) foo 
WHERE (geometrytype
     (st_collectionhomogenize
           (foo.geometry)
     ) <> 'LINESTRING'::text
);

