BEGIN
	UPDATE distribution.pipe SET _length3d = _length2d WHERE id = NEW.id AND NEW.tunnel_or_bridge IS TRUE;
	UPDATE distribution.pipe SET _length3d = NULL      WHERE id = NEW.id AND NEW.tunnel_or_bridge IS FALSE;
	RETURN NEW;
END;

